mapper.update:
update room
name=#{name}, typeid=#{typeid}, price=#{price}, area=#{area}, photo=#{photo}, status=#{status}, facilities=#{facilities},
where roomid =#{roomid}

update room
set status=#{status}
where number = #{number}

update room
set isdelist = #{isdelsit}
where roomid = #{roomid}

mapper.select:
select roomid, number, name, price, status, area, facilities, typename, photo, room.typeid as typeid, companyname, address, phone from room join roomtype on room.typeid = roomtype.typeid join company on room.userid = company.userid
isdelist = 0 and name like concat('%',#{name},'%') and number like concat('%',#{number},'%') and facilities like concat('%',#{facilities},'%') and typename =#{typename} and price >=#{low} and #{high}>=price and status =#{status} and companyname like concat('%',#{companyname},'%')
and address like concat('%',#{address},'%') and room.userid=#{userid}
